mapper.select:
SELECT b.id, b.title, b.title_en, b.abs, b.pubdate, b.author, b.publishername, b.isbn, b.classification, b.charcount, b.bookcode FROM book b WHERE 1 = 1
AND b.title LIKE #{search}
ORDER BY b.bookyear DESC

mapper.delete:
DELETE FROM book_details WHERE bookcode IN
#{bookCode}
AND id IN
#{id}

mapper.select:
SELECT bd.id, bd.zid, bd.fid, bd.title, bd.TextContent, bd.HtmlContent, bd.entry, bd.source, bd.bookcode, bd.zid_title AS zidTitle, bd.classify, bd.exdate, bd.people, bd.exdata, bd.exarea, bd.abs, CONCAT( bd.bookname, bd.bookyear, "Âç∑" ) AS bookname FROM book_details bd WHERE bd.entry = #{entry} AND bd.classify="ct"
AND bd.source = #{state}
AND bd.bookname LIKE #{myNameEntry}
AND bd.bookyear LIKE #{myYearEntry}
AND bd.title LIKE #{myTitle}
ORDER BY bd.bookyear DESC LIMIT #{currPage}, #{pageSize}

